import React, { useEffect } from "react";
import { loadGLTFModel } from "./modelLoader"; // your model loading function
import * as THREE from "three";

const furnitureList = [
  "./dylan_armchair.glb",
  "./ivan_armchair.glb",
  "./wood_round_table.glb",
  "./wood_coffee_table.glb",
  "./shell_armchair.glb",
  "./nesting_table.glb",
  "./standing_lamp.glb",
  "./plant_decor.glb",
  "./little_bookcase.glb",
  "./dining_set.glb"
];

function App() {
  useEffect(() => {
    let scene, camera, renderer;
    let selectedModel = null;

    const init = () => {
      scene = new THREE.Scene();
      camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
      camera.position.z = 5;

      renderer = new THREE.WebGLRenderer({ antialias: true });
      renderer.setSize(window.innerWidth, window.innerHeight);
      document.body.appendChild(renderer.domElement);

      // Light
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
      scene.add(ambientLight);

      // Setup furniture selection
      setupFurnitureSelection();

      animate();
    };

    const setupFurnitureSelection = () => {
      const selectionBar = document.createElement("div");
      selectionBar.style.position = "absolute";
      selectionBar.style.bottom = "0";
      selectionBar.style.width = "100%";
      selectionBar.style.background = "#333";
      selectionBar.style.display = "flex";
      selectionBar.style.justifyContent = "center";
      selectionBar.style.gap = "16px";
      selectionBar.style.padding = "10px";
      selectionBar.style.overflowX = "auto";
      selectionBar.style.zIndex = "100";

      furnitureList.forEach((path, index) => {
        const name = path.split("/").pop().replace(".glb", "");
        const imagePath = `thumbs/${name}.png`;

        const wrapper = document.createElement("div");
        wrapper.style.textAlign = "center";
        wrapper.style.color = "white";

        const img = new Image();
        img.src = imagePath;
        img.alt = name;
        img.style.width = "100px";
        img.style.height = "100px";
        img.style.cursor = "pointer";
        img.onerror = () => {
          // Fallback to button if image fails to load
          const btn = document.createElement("button");
          btn.innerText = name;
          btn.style.width = "100px";
          btn.style.height = "100px";
          btn.style.cursor = "pointer";
          btn.style.fontSize = "12px";
          btn.style.background = "#555";
          btn.style.color = "white";
          btn.onclick = () => loadModel(path);
          wrapper.appendChild(btn);
        };

        img.onload = () => {
          img.onclick = () => loadModel(path);
          wrapper.appendChild(img);
        };

        selectionBar.appendChild(wrapper);
      });

      document.body.appendChild(selectionBar);
    };

    const loadModel = (path) => {
      if (selectedModel) {
        scene.remove(selectedModel);
      }
      loadGLTFModel(path, (model) => {
        selectedModel = model;
        selectedModel.position.set(0, 0, 0);
        scene.add(selectedModel);
      });
    };

    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };

    init();
  }, []);

  return null;
}

export default App;
